import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

function Feedback() {
  const [form, setForm] = useState({ name: '', college_id: '', message: '' });
  const navigate = useNavigate();

  const handleChange = e => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async e => {
  e.preventDefault();

  const { name, college_id, message } = form;

  // Manual validation
  if (!name.trim() || !college_id.trim() || !message.trim()) {
    alert("Please fill in all fields before submitting.");
    return;
  }

  try {
    await axios.post('http://localhost:8000/api/feedback/', form);
    navigate('/thankyou');
  } catch (err) {
    console.error("Error submitting feedback:", err);
    alert("There was an error. Please try again later.");
  }
};


  return (
    <div style={styles.container}>
      <form onSubmit={handleSubmit} style={styles.form} noValidate>
        <h2 style={styles.title}>Submit Your Feedback</h2>

        <input
          name="name"
          placeholder="👤 Your Name"
          value={form.name}
          onChange={handleChange}
          required
          style={styles.input}
        />

        <input
          name="college_id"
          placeholder="🎓Course"
          value={form.college_id}
          onChange={handleChange}
          required
          style={styles.input}
        />

        <textarea
          name="message"
          placeholder="📝 We’d love to hear your thoughts..."
          value={form.message}
          onChange={handleChange}
          required
          rows={5}
          style={styles.textarea}
        />

        <button type="submit" style={styles.button}>Submit</button>
      </form>

      {/* Media Query Styling */}
      <style>{`
        @media (max-width: 500px) {
          form {
            padding: 1.2rem !important;
          }
        }

        button:hover {
          background-color: #0097a7;
        }
      `}</style>
    </div>
  );
}

const styles = {
  container: {
    minHeight: '100vh',
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    background: 'linear-gradient(to right, #e0f7fa, #fce4ec)',
    padding: '2rem'
  },
  form: {
    backgroundColor: '#fff',
    padding: '2rem',
    borderRadius: '12px',
    boxShadow: '0 8px 24px rgba(0,0,0,0.1)',
    width: '100%',
    maxWidth: '500px',
    display: 'flex',
    flexDirection: 'column',
    gap: '1rem'
  },
  title: {
    textAlign: 'center',
    marginBottom: '1rem',
    fontSize: '1.6rem',
    color: '#2c3e50'
  },
  input: {
    padding: '0.8rem',
    fontSize: '1rem',
    borderRadius: '8px',
    border: '1px solid #ccc'
  },
  textarea: {
    padding: '0.8rem',
    fontSize: '1rem',
    borderRadius: '8px',
    border: '1px solid #ccc',
    resize: 'vertical'
  },
  button: {
    padding: '0.9rem 1.5rem',
  fontSize: '1.05rem',
  background: 'linear-gradient(to right, #00bcd4, #00acc1)',
  color: '#fff',
  border: 'none',
  borderRadius: '10px',
  cursor: 'pointer',
  boxShadow: '0 4px 12px rgba(0, 188, 212, 0.3)',
  transition: 'all 0.3s ease',
  fontWeight: '600',
  letterSpacing: '0.5px',
  }
};

export default Feedback;
